extension.execute:
var getURL = properties.getURL;
var message = properties.message;
var query = { form: { "message": message} };

ajax ({
    url : getURL,
    type : "POST",
    data : query,
    timeout : 5000,
    success : function ( contents ) {
		log("GET sent. " + getURL);
		callbackSuccess( {
                    resultType : "continue"
		} );

    },
    error : function ( request, errorMessage ) {
        log("GET error. " + getURL);
        callbackSuccess( {
                    resultType : "continue"
		} );
    }
});

return {
    resultType : "pause"
};
